mapper.select:
			SELECT COUNT(*) totCnt
			FROM MEMBER
			WHERE 1=1
			AND EXISTS (SELECT MEMBER_ID FROM MEMBER WHERE MEMBER_ID = #{memberId}

            AND PASSWORD = #{password}
        )

SELECT 
	*
FROM MEMBER
WHERE 1=1
AND MEMBER_ID = #{memberId}
AND PASSWORD = #{password}

mapper.insert:

	SELECT IFNULL(MAX(MEMBER_NO), 10000)+1 FROM MEMBER   

