mapper.select:

SELECT 
	A.*,
	CASE STATE  
        WHEN 'S' THEN '대기'
        WHEN 'Y' THEN '승인'
        WHEN 'N' THEN '보류' 
        ELSE '대기'
    END AS STATE_NM
FROM MEMBER A
WHERE 1=1
AND USE_FLAG = #{useFlag}



	AND MEMBER_NAME = #{searchText}


	AND MEMBER_ID = #{searchText}


	AND EMAIL = #{searchText}


ORDER BY MEMBER_NO DESC

LIMIT #{startIdx}, #{endIdx}


SELECT
	COUNT(MEMBER_ID)AS totCnt
FROM MEMBER
WHERE 1=1
AND USE_FLAG = #{useFlag}



	AND MEMBER_NAME = #{searchText}


	AND MEMBER_ID = #{searchText}


	AND EMAIL = #{searchText}

SELECT A.*, CASE STATE WHEN 'S' THEN '대기' WHEN 'Y' THEN '승인' WHEN 'N' THEN '보류' ELSE '대기' END AS STATE_NM FROM MEMBER A WHERE 1=1
AND MEMBER_ID = #{memberId}
AND MEMBER_NO = #{memberNo}
